import { useState } from "react";
import "./Display.css";
const Display = ({ contract, account }) => {
  const [data, setData] = useState("");
  const getdata = async () => {
    let dataArray;
    const Otheraddress = document.querySelector(".address").value;
    try {
      if (Otheraddress) {
        dataArray = await contract.display(Otheraddress);
        console.log(dataArray);
      } else {
        dataArray = await contract.display(account);
      }
    } catch (e) {
      alert("You don't have access");
    }
    const isEmpty = Object.keys(dataArray).length === 0;

    if (!isEmpty) {
      const str = dataArray.toString();
      const str_array = str.split(",");
      
      const files = str_array.map((item, i) => {
        const url = `https://azure-fashionable-lark-246.mypinata.cloud/ipfs/${item.substring(6)}`;
        const fileExtension = item.split(".").pop().toLowerCase(); // Get the file extension

        if (["jpg", "jpeg", "png", "gif"].includes(fileExtension)) {
          // If the file is an image
          return (
            <a href={url} key={i} target="_blank" rel="noopener noreferrer">
              <img src={url} alt="file" className="image-list" />
            </a>
          );
        } else if (fileExtension === "pdf") {
          // If the file is a PDF
          return (
            <a href={url} key={i} target="_blank" rel="noopener noreferrer">
              <embed src={url} type="application/pdf" className="pdf-preview" />
              <p>PDF File - Click to view</p>
            </a>
          );
        } else {
          // For other types of files
          return (
            <a href={url} key={i} target="_blank" rel="noopener noreferrer">
              <p>Unknown File Type - Click to Download</p>
            </a>
          );
        }
      });
      
      setData(files);
    } else {
      alert("No files to display");
    }
  };
  return (
    <>
      <div className="image-list">{data}</div>
      <input
        type="text"
        className="address"
        placeholder="Enter Address"
        
      ></input>
      <button className="center button" onClick={getdata}>
        RETRIEVE
      </button>
    </>
  );
};
export default Display;
